        // o: 4321
        // n: 1342
        System.out.println(solution.minInteger("4321", 4));

        // k: 1
        // o: 100
        // n: 010
        System.out.println(solution.minInteger("100", 1));

        // k: 1000
        // o: 36789
        // n: 36789
        System.out.println(solution.minInteger("36789", 1000));

        // k: 23
        // o: 23
        // n: 23
        System.out.println(solution.minInteger("23", 23));

        // k: 23
        // o: 9438957234785635408
        // n: 0345989723478563548
        System.out.println(solution.minInteger("9438957234785635408", 23));

        // k: 2
        // old: 858957
        // new: 588597
        System.out.println(solution.minInteger("858957", 2));

